mapper.insert:
INSERT INTO MEMBERS values (
#{email}
,#{nickname}
,#{password}
,#{username}
,#{birth}
,#{phone}
,null
,#{address1}
,#{address2}
,#{postnumber}
,SYSDATE
,'USER'
,0
,null
)

	SELECT 'ME' || LPAD(MEMBERS_SEQ.CURRVAL, 8, '0') FROM dual

mapper.select:
SELECT *
FROM MEMBERS
WHERE USERNAME=#{username} AND PHONE=#{phone}

SELECT LAST_LOGIN_DATE
FROM MEMBERS
WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBERS
SET LAST_LOGIN_DATE = SYSDATE
WHERE EMAIL = #{email}
