mapper.update:
UPDATE MEMBERS
SET JOIN_COUNT = JOIN_COUNT + 1
WHERE EMAIL = #{email}

mapper.select:
SELECT *
FROM MEMBERS
WHERE USERNAME=#{param1} AND PHONE=#{param2}

SELECT LAST_LOGIN_DATE
FROM MEMBERS
WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBERS
SET LAST_LOGIN_DATE = SYSDATE
WHERE EMAIL = #{email}

mapper.select:
SELECT *
FROM MEMBERS
WHERE USERNAME=#{param1} AND PHONE=#{param2} AND EMAIL=#{param3}

mapper.update:
UPDATE MEMBERS SET
PASSWORD = #{password}
WHERE EMAIL = #{email}
